import { useReducer } from "react";
import reducer, { SET_USER } from "reducers/application";

export default function useApplicationData() {
  const [state, dispatch] = useReducer(reducer, {
    user: { name: "" },
    genres: {},
    favorite_movies: {},
    later_movies: {}
  });

  const setUser = name => dispatch({ type: SET_USER, value: name });


  return { state, setUser };
};
