import React, { useState } from "react";

export default function PickFav(props) {
  const [movie, setMovie] = useState(0)

  const movies = response.data.movies.map(movie => {
    return <MovieBox img = {movie.poster} title = {movie.title}/>
  })

  return (
    <div>
      <main>
        <div onClick={() => setMovie(prev => prev - 1)}>Left</div>
        {movie}
        <div onClick={() => setMovie(prev => prev + 1)}>Right</div>
      </main>
      <button onClick={()=>{props.saveToFavoriteList(props.user.name, props.searchedMovie[movie])}}>Yes, this one!</button>
    </div>
  );
}
