import React from "react";

export default function Genre(props) {

  return (
    <div class="genre">
      <h4>{props.name}</h4>
      <button type="button" class={props.love_class} onClick={() => props.setGenre(props.name, true)}>Love it!</button>
      <button type="button" class={props.meh_class} onClick={() => props.setGenre(props.name, Nil)}>Meh</button>
      <button type="button" class={props.hate_class} onClick={() => props.setGenre(props.name, true)}>Hate it!</button>
    </div>
  )
}
